import React, { Component } from "react";

export default class Searchbar extends Component {
  state = { term: "" };
  render() {
    return (
      <div>
        <form>
          <input
            onChange={(e) => {
              this.setState({ term: e.target.value });
            }}
            value={this.state.term}
            placeholder="Enter search term"
            type="text"
          />
        </form>
      </div>
    );
  }
}
